char	rot(char c, int n)
{
	if (!c)
		return (c);
	if (c - n < 'A')
		return (c - n + ('Z' - 'A') + 1);
	return (c - n);
}

int	n_strcmp(int a, char *s1, char *s2)
{
	while (*s1)
	{
		if (*s1 != rot(*s2, a))
			return ((unsigned char)*s1 - (unsigned char)rot(*s2, a));
		s1++;
		s2++;
	}
	return ((unsigned char)*s1 - (unsigned char)rot(*s2, a));
}


