int	main(int ac, char **av)
{
	if (ac == 1)
	{
		if (access(challenge_tab[0].new_file, F_OK) == -1)
			return (first_challenge());
		else
			return (usage());
	}
	else if (ac == 2)
	{
		if (strcmp(av[1], "help") == 0)
			return (help());
		else if (strcmp(av[1], "BEG[NN[NG") == 0)
			return (ch2_help());
		for (int i = 0; i < 7; i++)
			if (n_strcmp(i + 5, av[1], challenge_tab[i].code_word) == 0 )
			{
				if (access(challenge_tab[i - 1].new_file, F_OK) != -1 && access(challenge_tab[i].new_file, F_OK) == -1)
				{
					challenge_tab[i].f();
					return (0);
				}
				return (usage());
			}
		return (usage());
	}
	return (usage());
}
